import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    axios
      .get('https://localhost:7150/api/Product/GetAll')
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  }, []);

  const addToCart = (product) => {
    // Implement your cart functionality here, e.g., add the selected product to the cart state.
    console.log('Adding to cart:', product);
  };

  const openProductDetails = (product) => {
    setSelectedProduct(product);
    setIsModalOpen(true);
  };

  const closeProductDetails = () => {
    setSelectedProduct(null);
    setIsModalOpen(false);
  };

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Product List</h1>
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {products.map((product) => (
          <div key={product.id} className="col">
            <div className="card h-100">
              <img src={product.imageUrl} className="card-img-top" alt={product.productName} />
              <div className="card-body">
                <h5 className="card-title">{product.productName}</h5>
                <p className="card-text">Price: ${product.price}</p>
                <button className="btn btn-primary" onClick={() => openProductDetails(product)}>
                  View
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeProductDetails}
        className="modal-dialog modal-dialog-centered"
      >
        {selectedProduct && (
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">{selectedProduct.productName}</h5>
              <button type="button" className="close" onClick={closeProductDetails}>
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <img
                src={selectedProduct.imageUrl}
                alt={selectedProduct.productName}
                className="img-fluid rounded"
              />
              <p className="mt-3">Price: ${selectedProduct.price}</p>
            </div>
            <div className="modal-footer">
              <button className="btn btn-primary" onClick={() => addToCart(selectedProduct)}>
                Add to Cart
              </button>
              <button className="btn btn-secondary" onClick={closeProductDetails}>
                Close
              </button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default ProductList;
