import React from 'react';
import Modal from 'react-modal';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './ProductDetails.css'; // Import the custom CSS file

const ProductDetails = ({ product, addToCart, onClose }) => {
  return (
    <Modal
      isOpen={true}
      onRequestClose={onClose}
      className="modal-dialog modal-dialog-centered" // Center the modal
    >
      <div className="modal-content product-modal-content">
        <div className="modal-header product-modal-header">
          <h5 className="modal-title">{product.productName}</h5>
          <button type="button" className="close" onClick={onClose}>
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div className="modal-body product-modal-body">
          <img src={product.imageUrl} alt={product.productName} className="img-fluid" />
          <p>Price: ${product.price}</p>
        </div>
        <div className="modal-footer product-modal-footer">
          <button className="btn btn-primary" onClick={() => addToCart(product)}>
            Add to Cart
          </button>
          <button className="btn btn-secondary" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </Modal>
  );
};

export default ProductDetails;
