import React from 'react';
import Modal from 'react-modal';

const ProductDetails = ({ product, addToCart, onClose }) => {
  return (
    <Modal
      isOpen={true} // Set this to a state variable to control modal visibility
      onRequestClose={onClose} // Function to close the modal when clicking outside or pressing ESC
    >
      <div>
        <h3>{product.productName}</h3>
        <img src={product.imageUrl} alt={product.productName} />
        <p>Price: ${product.price}</p>
        <button onClick={() => addToCart(product)}>Add to Cart</button>
        <button onClick={onClose}>Close</button>
      </div>
    </Modal>
  );
};

export default ProductDetails;
