import React from 'react';
import Modal from 'react-modal';
import './ProductDetails.css'; // Create a new CSS file for custom styling

const ProductDetails = ({ product, addToCart, onClose }) => {
  return (
    <Modal
      isOpen={true}
      onRequestClose={onClose}
      className="product-modal" // Apply custom CSS class to the modal content
      overlayClassName="product-modal-overlay" // Apply custom CSS class to the modal overlay
    >
      <div className="container">
        <div className="row">
          <div className="col">
            <h3>{product.productName}</h3>
            <img src={product.imageUrl} alt={product.productName} className="img-fluid" />
            <p>Price: ${product.price}</p>
          </div>
        </div>
        <div className="row justify-content-end">
          <div className="col-auto">
            <button className="btn btn-primary mr-2" onClick={() => addToCart(product)}>
              Add to Cart
            </button>
            <button className="btn btn-secondary" onClick={onClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    </Modal>
  );
};

export default ProductDetails;
